import { useState, useEffect } from "react";
import CreateTask from './CreateTask';
import EditTask from './EditTask';
import './tasklist.css';
const axios = require("axios");

export default function Tasklist(){
    
    const [servicenowData, setServicenowData] = useState([]);
    const [modalVisibility, setmodalVisibility] = useState(false);
    const [editModalVisibility, setEditModalVisibility] = useState(false);
    const [editTaskData, setEditTaskData] = useState({});

    // Creating Tasks Modal
    const closeModal = (value) => {
        setmodalVisibility(value);
    }

    const showModal = () => {
      setmodalVisibility(true);
    };
  
    // Editing Tasks Modal
    const showEditModal = (element) => {
      setEditModalVisibility(true);
      setEditTaskData(element)
    }

    const closeEditModal = (value) => {
      setEditModalVisibility(value)
    }


    // REST API Get Method
  const getData = () => {
    var qs = require("qs");
    var data = qs.stringify({
      grant_type: "password",
      client_id: "f5310f090dfd11107f17158b75eeaff2",
      client_secret: "<UpvsoyH6@",
      username: "rest.user",
      password: "Ac}}v2qC%2dTlFn#p@",
    });
    var config = {
      method: "get",
      url: "https://dev75752.service-now.com/api/x_723118_dashboard/task_list",
      headers: {
        Authorization:
          "Bearer EKxdxkUtM77HYh2KEwnsvZ3q2--dqUCuVDC94k-4NWz3-sjxJt64YInsFxhAi-aNgnPhgBNLxJkfPNwA2sIpCA",
        "Content-Type": "application/x-www-form-urlencoded",
        //'Cookie': 'BIGipServerpool_dev75752=2609600522.40766.0000; JSESSIONID=B70C1F3F98D67BA5DC7AACD6D7684D4C; glide_session_store=F505655997F55110A6C0341E6253AFF2; glide_user_activity=U0N2M18xOnZsNVIwei9YZFU5cmRNQy9xZDlkV3VBL09wR0M0SC9qVW1qSldqczlua3M9Onh2Q2lDaEFVUXQ3Qnp4R2NwN1k3Rk9sRWE0andyZ1dHUUtzRTZYN0VLVFE9; glide_user_route=glide.80b7fa92314a6346bf2701360a9b4edb'
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        const retrievedData = response.data.result;
        setServicenowData(retrievedData);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

    // REST API Delete Method
  const deleteTask = (sys_id) => {
    var qs = require("qs");
    var data = qs.stringify({
      grant_type: "password",
      client_id: "f5310f090dfd11107f17158b75eeaff2",
      client_secret: "<UpvsoyH6@",
      username: "rest.user",
      password: "Ac}}v2qC%2dTlFn#p@",
    });
    var config = {
      method: "delete",
      url: `https://dev75752.service-now.com/api/x_723118_dashboard/task_list/${sys_id}`,
      headers: {
        Authorization:
          "Bearer EKxdxkUtM77HYh2KEwnsvZ3q2--dqUCuVDC94k-4NWz3-sjxJt64YInsFxhAi-aNgnPhgBNLxJkfPNwA2sIpCA",
        "Content-Type": "application/x-www-form-urlencoded",
        // Cookie:
        //   "BIGipServerpool_dev75752=2609600522.40766.0000; JSESSIONID=621B03BE04A332B4E675264BAAE3A3D1; glide_session_store=68F4963097825110A6C0341E6253AF49; glide_user_activity=U0N2M18xOmZjSGQxaGM0TEZ2NHg3aXpTQk52N0NicHRlZnB6Vk55SytVZTNQa1lNTTA9OkxPcHZyOVRRNmRvVDg0VHB3WUhySDFSUWRmbkYxZ0EvZEdtTnVVZHBHVEk9; glide_user_route=glide.80b7fa92314a6346bf2701360a9b4edb",
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        getData();
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(() => {
    getData();
  }, []);

    return (
        <div className="wrapper">
        <h3>TASK LIST APP</h3>

        <div className="tableContainer">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">TITLE</th>
                <th scope="col">DETAILS</th>
                <th scope="col">DUE DATA</th>
                <th scope="col"></th>
                <th scope="col"></th>
              </tr>
            </thead>
            <tbody>
              {servicenowData.map((element) => (
                <tr key={element.number}>
                  <td>{element.title}</td>
                  <td>{element.details}</td>
                  <td>{element.date}</td>
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => {
                        deleteTask(element.sys_id);
                      }}
                    >
                      DELETE
                    </button>
                  </td>
                  <td>
                    <button className="btn btn-danger" onClick={()=>{
                      showEditModal(
                      {
                      "title" : element.title,
                      "details" : element.details,
                      "date" : element.date,
                      "sys_id" : element.sys_id
                      }
                      )}}>EDIT</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {/*End of Table container*/}
        </div>

        <button
          className="btn btn-success"
          onClick={() => {
            showModal();
          }}
        >
          CREATE TASK
        </button>
        
    {modalVisibility &&  <CreateTask modalVisibility={closeModal} getData = {getData}/>}
    {editModalVisibility && <EditTask modalVisibility={closeEditModal} editTask={editTaskData} getData = {getData}/>}   

        {/*End of Wrapper*/}
      </div>
      
    );
}
