import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import "bootstrap/dist/js/bootstrap.bundle";
import { useState, useEffect } from "react";
const axios = require("axios");

function App() {
  const [servicenowData, setServicenowData] = useState([]);

  const getData = () => {
    var qs = require("qs");
    var data = qs.stringify({
      grant_type: "password",
      client_id: "f5310f090dfd11107f17158b75eeaff2",
      client_secret: "<UpvsoyH6@",
      username: "rest.user",
      password: "Ac}}v2qC%2dTlFn#p@",
    });
    var config = {
      method: "get",
      url: "https://dev75752.service-now.com/api/x_723118_dashboard/task_list/x_723118_dashboard_task_list",
      headers: {
        Authorization:
          "Bearer Mxt0E3uY8nAOKyUaJLPXNI5j9GXayZPFSS5JPc3J1Fbfr4pL7A0BAUxTugd-yaotX4xSSDAsN_7pDiEi-jZVbA",
        "Content-Type": "application/x-www-form-urlencoded",
        //'Cookie': 'BIGipServerpool_dev75752=2609600522.40766.0000; JSESSIONID=B70C1F3F98D67BA5DC7AACD6D7684D4C; glide_session_store=F505655997F55110A6C0341E6253AFF2; glide_user_activity=U0N2M18xOnZsNVIwei9YZFU5cmRNQy9xZDlkV3VBL09wR0M0SC9qVW1qSldqczlua3M9Onh2Q2lDaEFVUXQ3Qnp4R2NwN1k3Rk9sRWE0andyZ1dHUUtzRTZYN0VLVFE9; glide_user_route=glide.80b7fa92314a6346bf2701360a9b4edb'
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        const retrievedData = response.data.result;
        console.log(retrievedData);
        setServicenowData(retrievedData);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="App">
      {/**/}

      <div className="wrapper">
        <h3>TASK LIST APP</h3>

        <div className="tableContainer">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">Title</th>
                <th scope="col">Details</th>
                <th scope="col">Due data</th>
              </tr>
            </thead>
            <tbody>
              {servicenowData.map((element) => (
                <tr>
                  <td key={element.number}>{element.title}</td>
                  <td>{element.details}</td>
                  <td>{element.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/*End of Wrapper*/}
      </div>
    </div>
  );
}
export default App;
