import './modal.css'
const axios = require("axios");

export default function Modal({modalVisibility, getData}){

    const postTask = () => {
        var data = JSON.stringify({
            grant_type: "password",
            client_id: "f5310f090dfd11107f17158b75eeaff2",
            client_secret: "<UpvsoyH6@",
            username: "rest.user",
            password: "Ac}}v2qC%2dTlFn#p@",
            title: "Created from React",
            details: "test details111",
            active: "true",
          });
      
          var config = {
            method: "post",
            url: "https://dev75752.service-now.com/api/x_723118_dashboard/task_list",
            headers: {
              Authorization:
                "Bearer ldAXFCcIrh8mqGSRDBDX5owEaxdtW8__2PiLlVYJePkZvrNcyN5hVc6aoSzdresdo40kMObzLFSvZgj3AkDrVQ",
              "Content-Type": "application/json",
            },
            data: data,
          };
      
          axios(config)
            .then(function (response) {
              console.log(JSON.stringify(response.data));
              getData();
            })
            .catch(function (error) {
              console.log(error);
            });
    
        }
    

    return (
        <div className="modalWrapper">
            
            <div className='modal-popup'>
                <button className='exitButton' onClick={(event) => {
                modalVisibility(false);
                
                }}>X</button>
                <p>Lorem ipsum</p>
                <button className='createTaskButton' onClick={() => {
                    postTask();
                    modalVisibility(false);
                }}>CREATE TASK</button>
            </div>
            

        </div>
    );
}
