import './createTask.css'
import {useState} from 'react';
const axios = require("axios");

export default function CreateTask({modalVisibility, getData}){

    const [title, setTitle] = useState('');
    const [details, setDetails] = useState('');
    const [dueDate, setDueDate] = useState('');

    //REST API Post Method
    const postTask = () => {
        var data = JSON.stringify({
            // grant_type: "password",
            // client_id: "f5310f090dfd11107f17158b75eeaff2",
            // client_secret: "<UpvsoyH6@",
            // username: "rest.user",
            // password: "Ac}}v2qC%2dTlFn#p@",
            title: title,
            details: details,
            dueDate: dueDate,
            active: "true",
          });
      
          var config = {
            method: "post",
            url: "https://dev80940.service-now.com/api/x_723118_dashboard/task_list",
            headers: {
              // 'Authorization': 'Bearer WIJpHnPnZhmn5TFKPzayZolwZ2W9TsxNv0Rj1UdpVFECKXV2BdjGMG0INye5IGoyyDyWVWXk9Eoz5Gv28n8CDA',
              'Content-Type': 'application/json', 
              'Authorization': 'Basic YWRtaW46ejdEYSExc3ElUktO', 
            },
            data: data,
          };
      
          axios(config)
            .then(function (response) {
              getData();
            })
            .catch(function (error) {
              console.log(error);
            });
    
        }
    

    return (
        <div className="modalWrapper">
            
            <div className='modal-popup'>
                <div className='exitButton'>
                <button 
                className=' btn' 
                onClick={(event) => {modalVisibility(false)}}>X</button>
                </div>

                <div>
                <form>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(event) => setTitle(event.target.value)}/>
                    
                    <label>Details:</label>
                    <input type="text" value={details} onChange={(event) => setDetails(event.target.value)}/>
                    
                    <label>Due Date:</label>
                    <input type="date" value={dueDate} onChange={(event) => setDueDate(event.target.value)}/>
                    
                    </form>
                </div>
                <div className='createTaskButton'>
                <button className='createTaskButton btn btn-success' onClick={() => {
                    postTask();
                    modalVisibility(false);
                }}>CREATE TASK</button>
                </div>
                
                
            </div>
            

        </div>
    );
}
