import './modal.css'
import {useState} from 'react';
const axios = require("axios");

export default function Modal({modalVisibility, getData}){

    const [title, setTitle] = useState('');
    const [details, setDetails] = useState('');
    const [dueDate, setDueDate] = useState('');

    const postTask = () => {
        console.log(title)
        var data = JSON.stringify({
            grant_type: "password",
            client_id: "f5310f090dfd11107f17158b75eeaff2",
            client_secret: "<UpvsoyH6@",
            username: "rest.user",
            password: "Ac}}v2qC%2dTlFn#p@",
            title: title,
            details: details,
            active: "true",
          });
      
          var config = {
            method: "post",
            url: "https://dev75752.service-now.com/api/x_723118_dashboard/task_list",
            headers: {
              Authorization:
                "Bearer ldAXFCcIrh8mqGSRDBDX5owEaxdtW8__2PiLlVYJePkZvrNcyN5hVc6aoSzdresdo40kMObzLFSvZgj3AkDrVQ",
              "Content-Type": "application/json",
            },
            data: data,
          };
      
          axios(config)
            .then(function (response) {
              console.log(JSON.stringify(response.data));
              getData();
            })
            .catch(function (error) {
              console.log(error);
            });
    
        }
    

    return (
        <div className="modalWrapper">
            
            <div className='modal-popup'>
                <button className='exitButton btn' onClick={(event) => {
                modalVisibility(false);
                
                }}>X</button>
                <div>
                <form>
                    <label>Title:
                    <input type="text" value={title} onChange={(event) => setTitle(event.target.value)}/>
                    </label>
                    <label>Details:
                    <input type="text" value={details} onChange={(event) => setDetails(event.target.value)}/>
                    </label>
                    <label>Due Date:
                    <input type="text" value={dueDate} onChange={(event) => setDueDate(event.target.value)}/>
                    </label>
                    <button className='createTaskButton btn btn-success' onClick={() => {
                    postTask();
                    modalVisibility(false);
                }}>CREATE TASK</button>
                    </form>
                </div>
                
                
            </div>
            

        </div>
    );
}
